      type: pg
      plan: startup-4
      maintenanceDow: sunday
      maintenanceTime: 23:00:00
      terminationProtection: true
      pg:
        - version: '13'
          backupSchedule: 04:00
          ipFilters:
            - 1.2.3.4/32
            - 5.6.7.8/32
          pgSettings:
            fn::toJSON:
              timezone: Europe/Zurich
